import React from 'react';
// import { DialogContent, DialogOverlay } from '@reach/dialog';
// import "@reach/dialog/styles.css";

const FilterModal = ( { options, resetFilters, onApply, onDismiss, clearFilter} ) => {
    return (
      <>
          <section className="filter-modal-wrapper">
            <div className="filter-modal-header">
              <button className='filter-btn' onClick={() => {
                resetFilters()
                clearFilter()}}>Reset Filters</button>
            </div>
            <div className="filter-modal-content">{options}</div>
            <div className="filter-modal-actions">
              <button className="filter-btn" onClick={onApply}>Apply Filters</button>
            </div>
          </section>
      </>
    );
  }
export default FilterModal;

