import { useState } from "react";
import "./Form.css";
import { useMutation } from '@apollo/client';
import { GET_POST, CREATE_POST } from '../..'


// import { postForm } from "../utils/postForm";

const initialState = {
  username: "",
  gender: "",
  age: 0,
  gradYear: "",
  program: "",
  degree: "",
  firstPosition: "",
  jobHuntDuration: 0,
  yearsOfExperience: 0,
  positionTitle: "",
  company: "",
  locationOfEmployment: "",
  typeOfEmployment: "",
  state: "",
  negotiation: "",
  salary: 0
}

const Form = () => {
  const [form, setForm] = useState(initialState)
  const [selectedRadio, setSelectedRadio] = useState(null)
  const [createPost, { loading , error, data }] = useMutation(CREATE_POST, {
    refetchQueries: [GET_POST]
  })

  if (error) {
    console.log('error from form', error)
  }

  if (loading) {
    console.log('loading from form', loading)
  }

  if (data) {
    console.log('data from form', data)
  }

  const handleChange = (e) => {
    // console.log('form values', form)
    // console.log('')
    e.target.classList.add("active");
    const { name, value } = e.target;
    // setForm((prevState) => ({ ...prevState, [name]: value }));
    setSelectedRadio()
    setForm((prevState) => ({ ...prevState, [name]: value }));
  }

  const submitForm = (e) => {
    e.preventDefault();
    console.log('form data', form)
    createPost({
      variables: {
        username: form.username,
        gender: form.gender,
        age: form.age,
        gradYear: form.gradYear,
        program: form.program,
        degree: form.degree,
        firstPosition: form.firstPosition,
        jobHuntDuration: form.jobHuntDuration,
        yearsOfExperience: form.yearsOfExperience,
        positionTitle: form.positionTitle,
        company: form.company,
        locationOfEmployment: form.locationOfEmployment,
        typeOfEmployment: form.typeOfEmployment,
        state: form.state,
        negotiation: form.negotiation,
        salary: form.salary
      }
    })
    clearForm();
  }

const clearForm = () => {
   setForm({initialState})
 }

