import React from 'react'
import Header from "../Header/Header";
import Cover from "../Cover/Cover";
import SlideDrawer from '../Form/SlideDrawer.js'
import Backdrop from '../Form/Backdrop.js'
import MainPage from '../Form/MainPage.js'
import { getSalaries } from "../../APICalls";
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  fetchMore,
  useQuery,
  gql
} from "@apollo/client";
import './App.css';

const GET_DATA = gql`
   query {
      posts {
        id
        name
        company
        salary
        previousEducation
        bootCampType
        devTitle
        comments
        state
        jobHuntDuration
        gender
        age
        yearsOfExperience
      }
    }
`;
class App extends React.Component {
   state = { 
     drawerOpen: false, 
     posts: [],
     error: ""
   }

drawerToggleClickHandler = () => {
    this.setState({
      drawerOpen: !this.state.drawerOpen
    })
  }
backdropClickHandler = () => {
    this.setState({
      drawerOpen: false
    })
  }

    componentDidMount = () => {
    const { loading, error, data } = fetchMore(GET_DATA);
      if (loading) return 'Loading...';
      if (error) return `Error! ${error.message}`;

  return (
    <select name="posts">
      {data.posts.map(post => (
        <p>
          `${post.name}`
        </p>
      ))}
    </select>
  )
  };

   render(){
      let backdrop;
      if(this.state.drawerOpen){
        backdrop = <Backdrop close={this.backdropClickHandler}/>;
       }
      return(
         <div>
          <Header />
          <Cover />
           <SlideDrawer show={this.state.drawerOpen} />
           { backdrop }
           <MainPage toggle={this.drawerToggleClickHandler} />
         </div>
      )
    }
}
export default App
