import React from "react";
import ReactDOM from "react-dom";
import { useSpring, animated as a } from "react-spring";

import "./Form.css";

const Form = () => {
  const [salaryFormStatus, displaySalaryForm] = React.useState(false);
  const [filterFormStatus, displayFilterForm] = React.useState(false);
  const contentProps = useSpring({
    opacity: salaryFormStatus ? 1 : 0,
    marginTop: salaryFormStatus ? -500 : 0
  });
  return (
    <div className="container">
      <div className="button-container">
        <button onClick={() => displaySalaryForm(a => !a)} className="button">
          Submit Salary
        </button>
        <button onClick={() => displayFilterForm(a => !a)} className="button">
          Filter
        </button>
      </div>
      {!salaryFormStatus ? (
        <div></div>
      ) : (
        <a.div className="box" style={contentProps}>
          <h1>salary form</h1>
        </a.div>
      )}
      {!filterFormStatus ? (
        <div></div>
      ) : (
        <a.div className="box" style={contentProps}>
          <h1>salary form</h1>
        </a.div>
      )}
    </div>
  );
};
export default Form
