import { useState } from 'react'
// import { salaryData } from './HardSalaryData'
import Card from "./Card/Card";
import {
  useQuery,
  gql
} from "@apollo/client";

const GET_POSTS = gql`
    query {
      posts {
        id
        company
        salary
        previousEducation
        devTitle
        state
        jobHuntDuration
        gender
        age
        yearsOfExperience
      }
    }
`;

function APICalls() {

  const { loading, error, data } = useQuery(GET_POSTS);
  // const [salaries, setSalaries] = useState([])

  const salaryCards = data.posts.map(post => {
    return (
        <Card
          salary={post.salary}
          gender={post.gender}
          jobHuntDuration={post.jobHuntDuration}
        />
      );
  })

  return (
    <div name="postsContainer">
    {loading && <p>'Loading...'</p>}
    {error && <p>Error! {error.message}</p>}
    {salaryCards}
    </div>
  );
}

export default APICalls


