import React from "react";
import ReactDOM from "react-dom";
import { useSpring, animated as a } from "react-spring";

import "./Form.css";

const Form = () => {
  const [formStatus, displayForm] = React.useState(false);
  const contentProps = useSpring({
    opacity: formStatus ? 1 : 0,
    marginTop: formStatus ? 0 : -500
  });
  return (
    <div className="container">
      <div className="button-container">
        <button onClick={() => displayForm(a => !a)} className="button">
          Submit Salary
        </button>
      </div>
      {!formStatus ? (
        <div></div>
      ) : (
        <a.div className="box" style={contentProps}>
          <h1>Hey there ! React Spring is awesome.</h1>
        </a.div>
      )}
    </div>
  );
};
export default Form
