import { useState, useEffect } from "react";
import { Route, Switch } from 'react-router-dom'
import Header from "../Header/Header";
import Cover from "../Cover/Cover";
import SlideDrawer from "../Form/SlideDrawer";
import Backdrop from "../Form/Backdrop";
import MainPage from "../Form/MainPage";
import SalaryCards from "../SalaryCards/SalaryCards";
import Loader from "../Loader/Loader";
import NoMatchError from "../NoMatchError/NoMatchError";
import NotFound from '../NotFound/NotFound';
import Error from '../Error/Error';
import FilterForm from '../FilterForm/FilterForm';
import Results from '../Results/Results';
import { useQuery } from '@apollo/client';
import { GET_POST } from '../..';
import "./App.css";

const App = () => {
  const [drawerOpen, setDrawerOpen] = useState(false)
  const [salaryPosts, setSalaryPosts] = useState([])
  const [filterPosts, setFilterPosts] = useState([])
  const [filterInput, setFilterInput] = useState('')
  const [filterError, setFilterError] = useState('')
  const [input, setInput] = useState('')
  const { data, loading, error } = useQuery(GET_POST)

const drawerToggleClickHandler = () => {
  setDrawerOpen(!drawerOpen)
};

const backdropClickHandler = () => {
  setDrawerOpen(false)
};

const handleFilterInput = (e) => {
  setFilterInput(e.target.value)
}

const handleInput = (e) => {
  setInput(e.target.value)
}

const clearFilterForm = (e) => {
  setFilterInput('')
  setFilterPosts([])
}

useEffect(() => {
  if (loading) {
    console.log('loading', loading)
  } else if (error) {
    console.log('error', error)
  } else {
    setSalaryPosts(data)
  }
}, [data])

const filterData = (filterInput, input) => {
  setFilterPosts([])
  setFilterError('')
  const prop = filterInput.toLowerCase()
  const matchCards = salaryPosts.posts.filter(post => post[prop] === input)
  if (matchCards.length !== 0) {
    setFilterPosts(matchCards)
  } else if (matchCards.length === 0) {
    setFilterError('No matches!')
  } else {
    return null;
  }
}

  return (
    <div>
      <Header />
      <Cover />
      <Route exact path="/" render={() => <MainPage toggle={drawerToggleClickHandler} />}/>
      <Route exact path="/" render={() => <SlideDrawer toggle={drawerToggleClickHandler} show={drawerOpen} />}/>
      {drawerOpen && <Backdrop close={backdropClickHandler} />}

      <Route exact path="/" render={() => <FilterForm
        filterInput={filterInput}
        handleFilterInput={handleFilterInput}
        input={input}
        handleInput={handleInput}
        filterData={filterData}
        clearFilterForm={clearFilterForm}/>}/>
      {loading && <Loader />}
      {!loading && error && <Error err={error} />}
      <Route exact path="/" render={() => {
        if (!loading && !error && filterPosts.length === 0 && !filterError) {
          <SalaryPosts data={data.posts} />
        } else if (!loading && !error && filterPosts.length !== 0) {
          <Results filterPosts={filterPosts} />
        }}}/>

    </div>
  );
}

export default App;
